assignments.value:
={{ 
  $json.response.reduce((acc, item) => {
    item.times.forEach(time => {
      const t = time.toDateTime();
      const date = t.format('y-M-d');
      const hour = t.format('H:mm');

      const existingDate = acc.find(group => group.date === date);
      if (existingDate) {
        existingDate.times.push(hour);
      } else {
        acc.push({ date: date, times: [hour] });
      }
    });
    return acc;
  }, [])
}}

