={{ 
  $json.data.map(event => {
    const newEvent = { ...event };

    newEvent.start = newEvent.start.toDateTime().minus($('baseInfo').item.json.utc, 'hours').format('y-M-d H:mm');
    newEvent.end = newEvent.end.toDateTime().minus($('baseInfo').item.json.utc, 'hours').format('y-M-d H:mm');

    return newEvent;
  })
}}

={{ 
  $json.data.slots.keys().map(date => {
    return {
      times: $json.data.slots[date].map(slot => {
        const s = slot.time.toDateTime().minus($('baseInfo').item.json.utc, 'hours');
        return s.toUTC();
      })
    };
  })
}}

